    public static void getAllCTG(String dna){
        int count = 0;
        int index = 0;
        int prev = 0;

        while(true){
            index = dna.indexOf("ctg", index);
            if (index == -1){
                break;
            }
            if((index - prev) % 3 == 0 ){
                count++;
            }
            prev = index;
            index+=3;
        }
        System.out.println("CTG count: " + count);
    }
