    public static int findStopCodon(String dnaStr,
                                    int startIndex,
                                    String stopCodon){
        int currIndex = dnaStr.indexOf(stopCodon, startIndex);
        while(currIndex != -1){
            if((currIndex - startIndex) % 3 == 0){
                return currIndex;
            }
            else {
                currIndex = dnaStr.indexOf(stopCodon,currIndex+1);
            }
        }
        return -1;
    }

    public static String findGene(String dna, int where){
        String dnaStrand = dna.toLowerCase();
        int startIndex = dnaStrand.indexOf("atg", where);
        if(startIndex == -1){
            return "";
        }
        int taaIndex = findStopCodon(dnaStrand, startIndex, "taa");
        int tagIndex = findStopCodon(dnaStrand, startIndex, "tag");
        int tgaIndex = findStopCodon(dnaStrand, startIndex, "tga");

        int minIndex = 0;
        if (taaIndex == -1 ||
                (tgaIndex != -1 && tgaIndex < taaIndex)){
            minIndex = tgaIndex;
        }
        else{
            minIndex = taaIndex;
        }

        if(minIndex == -1 ||
                (tagIndex != -1 && tagIndex < minIndex)){
            minIndex = tagIndex;
        }

        if(minIndex == -1){
            return "";
        }
        return dna.substring(startIndex, minIndex+3);
    }


    public static StorageResource getAllGenes(String dna){
        StorageResource geneList = new StorageResource();
        int startIndex = 0;
        while(true){
            //find the next gene after startIndex
            String currentGene = findGene(dna, startIndex);
            //if no gene found -> leave the loop
            if (currentGene.isEmpty()){
                break;
            }
            geneList.add(currentGene);
            //print the gene
            //System.out.println(currentGene);
            startIndex = dna.indexOf(currentGene, startIndex) + currentGene.length();
        }
        return geneList;
    }

    public static double cgRatio(String dna){
        int countCG = 0;

        for(int i = 0; i < dna.length(); i++){
            if (dna.toLowerCase().charAt(i) == 'c' || dna.toLowerCase().charAt(i) == 'g'){
                countCG++;
            }

        }
        return Double.valueOf(countCG)/Double.valueOf(dna.length());
    }

    public static void processGenes(StorageResource sr){
        int longCount = 0;
        int highCGCount = 0;
        int longest = 0;
        for(String s : sr.data()){
            if(s.length() > 60){
                //System.out.println("Longer than 60 chars: " + s);
                longCount++;
            }
            if(cgRatio(s) > 0.35){
                //System.out.println("CG ratio greater than 0.35: " + s);
                highCGCount++;
            }
            if(s.length() > longest){
                longest = s.length();
            }
        }
        System.out.println(longCount + " strings are longer than 60 characters");
        System.out.println(highCGCount + " strings have hive CG ratio");
        System.out.println("The longest gene is " + longest + " characters long");
        System.out.println("Total of " + sr.size() + " genes");
    }


